const express = require('express');
const app = express();
const http = require('http').Server(app);
const io = require('socket.io')(http);
var mysql = require('mysql');
app.use(express.urlencoded());

var username = null;

// maybe create the database so the host doesnt need to create it
var con = mysql.createConnection({
  host: "192.168.1.41",
  user: "root",
  database: "chat"
});

con.connect(function(err) {
    if (err) throw err;
    console.log("Connected!");
});

// reset db removing users and resetting auto increment
con.query("DELETE from active_users", function (err, result, fields) {
    if (err) throw err;
    console.log('db reseted')
});
con.query("ALTER TABLE active_users AUTO_INCREMENT = 1;", function (err, result, fields) {
    if (err) throw err;
});

app.get('/', function(req, res) {
    res.render('login.ejs');
});

app.post('/', function(req, res) {
    var repeated = false;
    username = req.body.username;

    //check if exists in the db
    con.query("SELECT usernames FROM active_users", function (err, result, fields) {
        if (err) throw err;
        for(var i = 0; i < result.length; i++) {
            if(result[i].usernames == username){
                repeated = true;
            }
        }
    });
    // should use callback instead of timeout
    setTimeout(function(){
        if(username && !repeated){
            con.query("INSERT INTO active_users (usernames) VALUES (?)", username,
            function (err, result) {
                if (err) throw err;
                console.log('user ' + username + " inserted in the db");
            });
            res.render('index.ejs');
        } else {
            if(repeated){
                res.render('login.ejs')
            }
            res.render('login.ejs')
            // return error message in case of empty field or duplicate
            
        }
    }, 100);
});

//timestamps
function now() {
    var str = "";

    var currentTime = new Date()
    var hours = currentTime.getHours()
    var minutes = currentTime.getMinutes()
    var seconds = currentTime.getSeconds()

    if (minutes < 10) {
        minutes = "0" + minutes
    }
    if (seconds < 10) {
        seconds = "0" + seconds
    }
    str += hours + ":" + minutes + ":" + seconds + " ";
    return str;
}

