const express = require('express');
const app = express();
const http = require('http').Server(app);
const io = require('socket.io')(http);


app.get('/', function(req, res) {
    res.render('index.ejs');
});

var activeUsers = [];
var cnt = 1;

//timestamps
function now() {
    var str = "";

    var currentTime = new Date()
    var hours = currentTime.getHours()
    var minutes = currentTime.getMinutes()
    var seconds = currentTime.getSeconds()

    if (minutes < 10) {
        minutes = "0" + minutes
    }
    if (seconds < 10) {
        seconds = "0" + seconds
    }
    str += hours + ":" + minutes + ":" + seconds + " ";
    return str;
}

io.sockets.on('connection', function(socket) {
    socket.on('username', function(username) {
        if(!username || username == 'null'){
            username = 'anon'+cnt;
            cnt++;
        } else{
            for(var i = activeUsers.length - 1; i >= 0; i--) {
                if(activeUsers[i] === username) {
                    username = 'anon'+cnt;
                    cnt++;
                }
            }
        }
        socket.username = username;
        io.emit('is_online', 'ðŸ”µ <i>' + socket.username + ' joined the chat.</i>');
        activeUsers.push(socket.username);
    });

    socket.on('disconnect', function(username) {
        io.emit('is_online', 'ðŸ”´ <i>' + socket.username + ' left the chat.</i>');
        for(var i = activeUsers.length - 1; i >= 0; i--) {
            if(activeUsers[i] === socket.username) {
                activeUsers.splice(i, 1);
            }
        }
    })

    socket.on('chat_message', function(message) {
        io.emit('chat_message', '[' + now() + '] ' + '<strong>' + socket.username + '</strong>: ' + message);
    });

    socket.on('typing', function(hasText) {
        if(hasText){
            io.emit('typing', '<i>Â Â Â ' + socket.username + " is typing..." + '</i>');
        } else {

            // here it should be checked whether another user was typing, if so leave their 'typing...' message, else remove

            io.emit('typing', "");
        }
    });

});

